import React, { useState } from "react";
import { Carousel } from "react-responsive-carousel";
import "react-responsive-carousel/lib/styles/carousel.min.css";
import { useCart } from "./CartContext";
import "./NasaFlowShop.css";

const productImages = [
  "/images/nasaflow-box.png",
  "/images/nasaflow-box.png",
  "/images/nasaflow-box.png",
];

export default function NasaFlowShop() {
  const { addToCart } = useCart();
  const [showNotif, setShowNotif] = useState(false);

  const handleAddToCart = () => {
    addToCart({
      id: 1,
      name: "Bandelette magn√©tique NasaFlow",
      price: 19.90,
      quantity: 1,
    });
    setShowNotif(true);
    setTimeout(() => setShowNotif(false), 2000);
  };

  return (
    <div className="nf-wrapper">
      {showNotif && (
        <div className="nf-notif">Produit ajout√© au panier üõí</div>
      )}

      <div className="nf-product-section">
        <div className="nf-gallery">
          <Carousel showThumbs={true} infiniteLoop autoPlay interval={5000}>
            {productImages.map((src, i) => (
              <div key={i}>
                <img src={src} alt={`Bandelette NasaFlow ${i + 1}`} className="nf-product-img" />
              </div>
            ))}
          </Carousel>
        </div>
        <div className="nf-product-info">
          <h1 className="nf-title">
            Bandelette magn√©tique <span className="nf-title-accent">NasaFlow</span>
          </h1>
          <ul className="nf-features">
            <li><span>‚ö°</span>Booste la respiration</li>
            <li><span>‚ôªÔ∏è</span>R√©utilisable et lavable</li>
            <li><span>üò¥</span>Id√©al pour sport et sommeil</li>
            <li><span>üöö</span>Livraison offerte</li>
          </ul>
          <div className="nf-reviews">
            <div className="nf-stars">‚òÖ‚òÖ‚òÖ‚òÖ‚òÖ</div>
            <div className="nf-review-count">+320 avis clients</div>
          </div>
          <div className="nf-price">CHF 19.90</div>
          <button className="nf-btn" onClick={handleAddToCart}>Ajouter au panier</button>
          <p className="nf-secure">Paiement 100% s√©curis√© ‚Ä¢ Stripe/CB</p>
          <div className="nf-socials">
            <a href="https://instagram.com/" target="_blank" rel="noopener noreferrer" title="Instagram">
              {/* Instagram SVG */}
              <svg width="36" height="36" fill="none" viewBox="0 0 32 32"><rect width="32" height="32" rx="8" fill="#FFD600"/><path d="M16 11.5a4.5 4.5 0 1 1 0 9 4.5 4.5 0 0 1 0-9Zm0-2C11.31 9.5 7.5 13.31 7.5 18s3.81 8.5 8.5 8.5 8.5-3.81 8.5-8.5-3.81-8.5-8.5-8.5Zm6.5 2.06a1.06 1.06 0 1 1-2.12 0 1.06 1.06 0 0 1 2.12 0Z" fill="#000"/></svg>
            </a>
            <a href="https://tiktok.com/" target="_blank" rel="noopener noreferrer" title="TikTok">
              {/* TikTok SVG */}
              <svg width="36" height="36" fill="none" viewBox="0 0 32 32"><rect width="32" height="32" rx="8" fill="#FFD600"/><path d="M21.6 10.4V14.8a5.6 5.6 0 1 1-5.6-5.6h1.6" stroke="#000" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/><path d="M19.2 8.8h2.4v2.4" stroke="#000" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/></svg>
            </a>
            <a href="https://facebook.com/" target="_blank" rel="noopener noreferrer" title="Facebook">
              {/* Facebook SVG */}
              <svg width="36" height="36" fill="none" viewBox="0 0 32 32"><rect width="32" height="32" rx="8" fill="#FFD600"/><path d="M18.5 28v-8h2.5l.5-4h-3V13c0-1.1.3-2 2-2h1.5V7.5S20.6 7 19.5 7c-3 0-4.5 1.5-4.5 4.5v2.5h-3v4h3v8" stroke="#000" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/></svg>
            </a>
            <a href="https://snapchat.com/" target="_blank" rel="noopener noreferrer" title="Snapchat">
              {/* Snapchat SVG */}
              <svg width="36" height="36" fill="none" viewBox="0 0 32 32"><rect width="32" height="32" rx="8" fill="#FFD600"/><path d="M16 11c-2.5 0-4 2-4 5s1.5 5 4 5 4-2 4-5-1.5-5-4-5zm0 0c1.5 0 2.5.5 3 1.5.5 1 0 2.5 0 2.5s1 .5 1 2c0 1.5-1 2-2.5 2s-2.5-.5-2.5-2c0-1.5 1-2 1-2s-.5-1.5 0-2.5C13.5 11.5 14.5 11 16 11z" fill="#000"/></svg>
            </a>
          </div>
        </div>
      </div>

      {/* T√©moignages anim√©s (fake/demo) */}
      <div className="nf-testimonials">
        <h2>Ils ont ador√© NasaFlow üòç</h2>
        <div className="nf-testi-cards">
          <div className="nf-testi-card">¬´‚ÄØJe respire tellement mieux la nuit‚ÄØ! ¬ª <br/><b>‚Äî Marion, Lausanne</b></div>
          <div className="nf-testi-card">¬´‚ÄØIndispensable √† l‚Äôentra√Ænement. Livraison rapide !‚ÄØ¬ª <br/><b>‚Äî Samir, Gen√®ve</b></div>
          <div className="nf-testi-card">¬´‚ÄØInvisible et super confortable. Je recommande !‚ÄØ¬ª <br/><b>‚Äî Clara, Lyon</b></div>
        </div>
      </div>
    </div>
  );
}
