import React, { useState } from "react";
import "./ContactPage.css"; // Crée le fichier pour le style ou copie le CSS du reste

export default function ContactPage() {
  const [sent, setSent] = useState(false);

  const handleSubmit = e => {
    e.preventDefault();
    // Ici tu peux mettre un vrai envoi mail avec emailjs ou autre
    setSent(true);
  };

  return (
    <div className="webicy-contact-page">
      <h1>Contactez Webicy</h1>
      <p>Tu veux discuter d’un projet, d’une idée, ou demander un devis ?</p>
      <form className="webicy-form" onSubmit={handleSubmit}>
        <input type="text" placeholder="Votre nom" required />
        <input type="email" placeholder="Votre email" required />
        <textarea placeholder="Expliquez votre besoin" required />
        <button type="submit">Envoyer</button>
      </form>
      {sent && <p style={{ color: "green", marginTop: "1rem" }}>Merci pour votre message ! Nous répondons très vite.</p>}
      <div style={{ marginTop: "2rem" }}>
        Ou directement : <a href="mailto:contact@webicy.ch">contact@webicy.ch</a>
      </div>
    </div>
  );
}
