import React from "react";
import { Routes, Route, Link } from "react-router-dom";
import NasaFlowShop from "./NasaFlowShop";
import CartPage from "./CartPage";
import { useCart } from "./CartContext";
import SuccessPage from "./SuccessPage";
import AdminPage from "./AdminPage";
import AdminPagePro from "./AdminPagePro";
import AdminLogin from "./AdminLogin";
import AboutPage from "./AboutPage";
import "./App.css"; // Ajoute du CSS moderne

import logo from "./assets/nasaflow-1.png";

const socialLinks = [
  { href: "https://www.instagram.com/", icon: "/instagram.svg", alt: "Instagram" },
  { href: "https://www.tiktok.com/", icon: "/tiktok.svg", alt: "TikTok" },
  { href: "https://www.facebook.com/", icon: "/facebook.svg", alt: "Facebook" },
];

function App() {
  const { cart } = useCart();
  const totalItems = cart.reduce((sum, item) => sum + item.quantity, 0);

  return (
    <>
      {/* Navbar */}
      <header className="navbar">
        <Link to="/" className="logo">
            <img src={logo} alt="Logo NasaFlow" className="logo-img"
            style={{ height: 44, marginRight: 16, verticalAlign: "middle" }}/>
          <span style={{ fontWeight: "bold", fontSize: 22, letterSpacing: 2 }}>
            NasaFlow
          </span>
        </Link>
        <nav>
          <Link to="/a-propos" className="nav-link">
            Ã€ propos
          </Link>
          <Link to="/panier" className="cart-button">
            ðŸ›’ Panier
            {totalItems > 0 && <span className="cart-badge">{totalItems}</span>}
          </Link>
        </nav>
        <div className="socials">
          {socialLinks.map(({ href, icon, alt }) => (
            <a key={href} href={href} target="_blank" rel="noopener noreferrer">
              <img src={icon} alt={alt} className="social-icon" />
            </a>
          ))}
        </div>
       </header>
      <Routes>
        <Route path="/" element={<NasaFlowShop />} />
        <Route path="/panier" element={<CartPage />} />
        <Route path="/success" element={<SuccessPage />} />
        <Route path="/admin" element={<AdminPage />} />
        <Route path="/admin-pro" element={<AdminPagePro />} />
        <Route path="/admin-login" element={<AdminLogin />} />
        <Route path="/a-propos" element={<AboutPage />} />
      </Routes>
    </>
  );
}
export default App;
